import React, { Component } from "react";
import Employees from "./Employees.js";

class Directory extends Component
{
    state = {
        employees: [],
        empSort: [],
        sorted: false,
    };

    componentDidMount=()=>{
        fetch(`https://randomuser.me/api/?results=25&nat=us&inc=name`)
        .then(res =>res.json())
        .then(json => {
            this.setState({ employees: json.results })
        })
    };
    sortEmp = () => {
        let { employees } = this.state;
        let empSort = employees.filter(sorted => {
            return (
                sorted.name.first.toLowerCase() || sorted.name.last.toLowerCase()
                )
        })
        this.setState({ empSort })
    }
        render = ()=>{
            return(
                <div>
        <div className="jumbotron">
          <h2 className="display-4">
            User Directory
          </h2>
        </div>

        <div className="container">
          <table className="table">
            <thead className="thead">
              <tr>
                <th>Name</th>
              </tr>
            </thead>
            <tbody>
              {!this.state.empSort.map(employee => (
                <Employees
                firstName = {employee.name.first}
                lastName = {employee.name.last} />
              ))};

            </tbody>
          </table>

        </div>
      </div >

    )

            }
          }

export default Directory;
